import React from "react";
import styles from "./Home.module.css";
import { Link } from "react-router-dom";

import Header from "../Common/Header/Header";
import Image from "../../assets/Home-Image.svg";

const Home = () => {
  return (
    <div className={styles.mainContainer}>
      <Header />
      <section className={styles.container}>
        <article className={styles.text}>
          <h1>
            Join or Create a New Class, Share Notes, and Submit Assignments!
          </h1>
          <div className={styles.links}>
            <Link to="/login" className={styles.callTo} aria-label="Login Page">
              Login
            </Link>
            <Link
              to="/signin"
              className={styles.signinLink}
              aria-label="Sign In Page"
            >
              Donâ€™t have an account? Sign in Now!
            </Link>
          </div>
        </article>
        <figure className={styles.image}>
          <img src={Image} alt="Illustration of students studying together" />
        </figure>
      </section>
    </div>
  );
};

export default Home;
